option.value:
@echo off
echo ============================================
echo  Exportar Base de Datos PostgreSQL
echo ============================================
echo.

set TIMESTAMP=%date:~-4%%date:~3,2%%date:~0,2%_%time:~0,2%%time:~3,2%%time:~6,2%
set TIMESTAMP=%TIMESTAMP: =0%

echo Creando dump de la base de datos...
docker exec -t mayorista_db_container-1 pg_dumpall -U postgres > dump_backup_%TIMESTAMP%.sql

echo.
echo ============================================
echo  Dump creado: dump_backup_%TIMESTAMP%.sql
echo ============================================
echo.
pause
